  //初始化日志文件
  FLAGS_log_dir = cmdparser.getLogDir();
  google::InitGoogleLogging(argv[0]);

  //启动consumer
  std::vector consumerThreads;
  for (int i = 0; i  lk(mut);
    keysQueue.push(line);
    cond.notify_one();
  }

  //生产者生产结束,通知消费者处理完后可以结束
  isShutdown.store(true);
